    public class Day4
    {
        private static readonly string WordTarget = "XMAS";
        private static readonly string WordTarget2 = "MAS";

        public static int Main4(string[] args)
        {
            var count = Day4.CountWord(Day4.Input, Day4.WordTarget2, true);
            Console.WriteLine($"count: {count}");
            Console.ReadKey();
            return 0;
        }

        private static int CountWord(string[] puzzle, string wordTarget, bool countXs = false)
        {
            if (puzzle.Length == 0 || puzzle[0].Length == 0)
            {
                return 0;
            }

            int count = 0;
            for (int row = 0; row < puzzle.Length; ++row)
            {
                for (int col = 0; col < puzzle[row].Length; ++col)
                {
                    if (!countXs)
                    {
                        count += CountWord(puzzle, wordTarget, row, col, 1, 0)
                               + CountWord(puzzle, wordTarget, row, col, 1, 1)
                               + CountWord(puzzle, wordTarget, row, col, 0, 1)
                               + CountWord(puzzle, wordTarget, row, col, -1, 1)
                               + CountWord(puzzle, wordTarget, row, col, -1, 0)
                               + CountWord(puzzle, wordTarget, row, col, -1, -1)
                               + CountWord(puzzle, wordTarget, row, col, 0, -1)
                               + CountWord(puzzle, wordTarget, row, col, 1, -1);
                    }
                    else
                    {
                        count += CountWordXs(puzzle, wordTarget, row, col);
                    }
                }
            }

            return count;
        }

        private static int CountWordXs(string[] puzzle, string wordTarget, int row, int col)
        {
            if (wordTarget.Length % 2 != 1)
            {
                return 0;
            }

            int wordTargetLengthHalf = wordTarget.Length / 2;
            int diagonals = CountWord(puzzle, wordTarget, row - wordTargetLengthHalf, col - wordTargetLengthHalf, 1, 1)
                          + CountWord(puzzle, wordTarget, row + wordTargetLengthHalf, col - wordTargetLengthHalf, -1, 1)
                          + CountWord(puzzle, wordTarget, row + wordTargetLengthHalf, col + wordTargetLengthHalf, -1, -1)
                          + CountWord(puzzle, wordTarget, row - wordTargetLengthHalf, col + wordTargetLengthHalf, 1, -1);
            return diagonals == 2 ? 1 : 0;
        }

        private static int CountWord(string[] puzzle, string wordTarget, int row, int col, int dirRow, int dirCol)
        {
            for (int i = 0; i < wordTarget.Length; ++i)
            {
                if (row < 0 || row >= puzzle.Length
                 || col < 0 || col >= puzzle[row].Length
                 || puzzle[row][col] != wordTarget[i])
                {
                    return 0;
                }

                row += dirRow;
                col += dirCol;
            }

            return 1;
        }


