    public class Day7
    {
        public static int Main7(string[] args)
        {
            string input = Day7.Input;
            Equation[] equations;
            string errorMessage;

            // parse the input
            if (!Day7.TryParse(input, out equations, out errorMessage))
            {
                Console.WriteLine(errorMessage);
                Console.ReadKey();
                return 1;
            }

            // sum valid equations' targets
            long sum = 0;
            foreach (var equation in equations)
            {
                if (equation.CanEqualTarget())
                {
                    sum += equation.target;
                }
            }

            Console.WriteLine($"sum: {sum}");
            Console.ReadKey();
            return 0;
        }

        private static bool TryParse(string input, out Equation[] equations, out string errorMessage)
        {
            equations = null;
            errorMessage = null;

            string[] lines = input.Split("\r\n");
            Equation[] equationsTemp = new Equation[lines.Length];
            for (int i = 0; i < lines.Length; ++i)
            {
                string[] pieces = lines[i].Split(": ");
                long num;
                if (!long.TryParse(pieces[0], out num)) // parse target
                {
                    errorMessage = $"Failed to parse long: '{pieces[0]}'";
                    return false;
                }
                long target = num;

                pieces = pieces[1].Split(" ");
                long[] operands = new long[pieces.Length];
                for (int j = 0; j < pieces.Length; ++j)
                {
                    if (!long.TryParse(pieces[j], out num)) // parse operands
                    {
                        errorMessage = $"Failed to parse long: '{pieces[j]}'";
                        return false;
                    }
                    operands[j] = num;
                }

                equationsTemp[i] = new Equation(target, operands);
            }

            equations = equationsTemp;
            return true;
        }

        private class Equation
        {
            public long target;
            public long[] operands;

            public Equation(long target, long[] operands)
            {
                this.target = target;
                this.operands = operands;
            }

            public bool CanEqualTarget()
            {
                // base cases
                if (this.operands.Length == 0)
                {
                    return this.target == 0;
                }
                if (this.operands.Length == 1)
                {
                    return this.target == this.operands[0];
                }

                if (this.operands.Length == 2)
                {
                    // check if any operator combines to the target
                    return this.target == this.operands[0] + this.operands[1]
                        || this.target == this.operands[0] * this.operands[1]
                        || this.target == Equation.Concatenate(this.operands[0], this.operands[1]);
                }

                // check if any operator yields an equation that can reach the target
                if (new Equation(this.target, this.CombineOperands(Operation.Add)).CanEqualTarget()
                 || new Equation(this.target, this.CombineOperands(Operation.Multiply)).CanEqualTarget()
                 || new Equation(this.target, this.CombineOperands(Operation.Concatenate)).CanEqualTarget())
                {
                    return true;
                }

                return false;
            }

            private enum Operation
            {
                None, // necessary/customary
                Add,
                Multiply,
                Concatenate
            }

            private long[] CombineOperands(Operation operation)
            {
                if (this.operands.Length < 3)
                {
                    return this.operands;
                }

                var operands = new long[this.operands.Length - 1];
                if (operation == Operation.Add)
                {
                    operands[0] = this.operands[0] + this.operands[1];
                }
                else if (operation == Operation.Multiply)
                {
                    operands[0] = this.operands[0] * this.operands[1];
                }
                else if (operation == Operation.Concatenate)
                {
                    operands[0] = Equation.Concatenate(this.operands[0], this.operands[1]);
                }
                else
                {
                    throw new InvalidOperationException($"Invalid operation: '{operation.ToString()}'");
                }

                // copy the rest of the operands (admit this is inefficient and lazier than passing an indexer)
                for (int i = 2; i < this.operands.Length; ++i)
                {
                    operands[i - 1] = this.operands[i];
                }

                return operands;
            }

            private static long Concatenate(long num1, long num2)
            {
                long pow = 1;
                long concatenated = 0;

                // evaluate num1
                long num =  num2;
                while (num > 9)
                {
                    int digit = (int)(num % 10);
                    concatenated += digit * pow;
                    num /= 10;
                    pow *= 10;
                }
                concatenated += num * pow;
                pow *= 10;

                // leave power of 10 and prepend num2 
                num = num1;
                while (num > 9)
                {
                    int digit = (int)(num % 10);
                    concatenated += digit * pow;
                    num /= 10;
                    pow *= 10;
                }
                concatenated += num * pow;

                return concatenated;
            }
        }

